import React, { Component } from "react";
import Newsitem from "./Newsitem";

export class News extends Component {
 
  constructor() {
    super();
    this.state = {
      articles: [],
      leading: false,
    };
  }

  async componentDidMount() {
    let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=1";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ articles: parsedData.articles });
  }

  handlePrevbtn = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=${this.state.page - 1}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles
    })
  }

  handleNextbtn = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=${this.state.page + 1}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({
      page: this.state.page + 1,
      articles: parsedData.articles
    })
  }

  render() {
    return (
      <div className="container my-4">
        <h2 className="my-4 display-6 bg-secondary py-2 px-3 text-light">Top Headlines - India</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4 my-3" key={element.url}>
                <Newsitem
                  title={element.title ? element.title.slice(0, 30) : ""}
                  description={element.description ? element.description.slice(0, 100) : ""}
                  imageurl={!element.urlToImage?"./download.jfif":element.urlToImage}
                  newsurl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="d-flex justify-content-center my-2 bg-light py-3">
          <button disabled={this.state.page<=1} type="button" className="mx-2 btn btn-dark" onClick={this.handlePrevbtn}>&#8592; Previous</button>
          <button type="button" className="mx-2 btn btn-dark" onClick={this.handleNextbtn}>Next &#8594;</button>
        </div>
      </div>
    );
  }
}

export default News;
