import React, { Component } from 'react'
import Newsitem from './Newsitem'

export class News extends Component {
    articles = [ ]

    constructor(){
        super();
        this.state = {
            articles: this.articles,
            leading: false
        }
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc";
        let data = await fetch(url);
        let parsedata = await data.json();
        this.setState({articles: parsedata.articles})
    }

    render() {
        return (
            <div className="container my-4">
                <h2 className="my-4">610 News - Top Headlines</h2>
                <div className="row">
                    {this.state.articles.map((element)=>{
                            return <div className="col-md-4 my-3" key={element.url}>
                            <Newsitem title={element.title?element.title.slice(0, 30):""} description={element.description?element.description.slice(0, 100):""} imageurl={element.urlToImage} newsurl={element.url}/>
                        </div>
                    })}
                </div>
            </div>
        )
    }
}

export default News
