              element={
                <News
                  setprogress={setProgress}
                  key="general"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="general"
                />
              }
            />
            <Route
              exact
              path="/business"
              element={
                <News
                  setprogress={setProgress}
                  key="business"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="business"
                />
              }
            />
            <Route
              exact
              path="/entertainment"
              element={
                <News
                  setprogress={setProgress}
                  key="entertainment"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="entertainment"
                />
              }
            />
