              exact
              path="/health"
              element={
                <News
                  setprogress={setProgress}
                  key="health"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="health"
                />
              }
            />
            <Route
              exact
              path="/science"
              element={
                <News
                  setprogress={setProgress}
                  key="science"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="science"
                />
              }
            />
            <Route
              exact
              path="/sports"
              element={
                <News
                  setprogress={setProgress}
                  key="sports"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="sports"
                />
              }
            />
            <Route
              exact
              path="/technology"
              element={
                <News
                  setprogress={setProgress}
                  key="technology"
                  pagesize={pageSize}
                  country={country}
                  api={api}
                  category="technology"
                />
              }
            />
          </Routes>
        </div>
      </Router>
    );

}
