import React, { Component } from "react";
import Newsitem from "./Newsitem";

export class News extends Component {
 
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1
    };
  }

  async componentDidMount() {
    let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=1&pageSize=9";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({ 
      articles: parsedData.articles, totalResults: parsedData.totalResults
     });
  }

  handlePrevClick = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=${this.state.page - 1}&pageSize=9`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles
    });
  }

  handleNextClick = async ()=>{
    if(this.state.page + 1 > Math.ceil(this.state.totalResults/9)){

    } else{
      let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=20ab4a01daa94a5f9b4d87dcd2249bdc&page=${this.state.page + 1}&pageSize=9`;
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles
      });
    }
  }

  render() {
    return (
      <div className="container my-4">
        <h2 className="mt-4 display-6 bg-secondary py-2 px-3 text-light">Top Headlines - India</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4 my-3" key={element.url}>
                <Newsitem
                  title={element.title ? element.title.slice(0, 30) : ""}
                  description={element.description ? element.description.slice(0, 100) : ""}
                  imageurl={!element.urlToImage?"./download.jfif":element.urlToImage}
                  newsurl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="d-flex justify-content-center my-2 bg-light py-3">
          <button disabled={this.state.page<=1} type="button" className="mx-2 btn btn-dark" onClick={this.handlePrevClick}>&#8592; Previous</button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/20)}type="button" className="mx-2 btn btn-dark" onClick={this.handleNextClick}>Next &#8594;</button>
        </div>
      </div>
    );
  }
}

export default News;
